<script setup>
    import axios from '@/axios.js';
    import { onMounted, ref } from 'vue';
    import { useRoute } from 'vue-router';
    import EloIcon from '@/icons/EloIcon.svg';
    import AmexIcon from '@/icons/AmexIcon.svg';
    import VisaIcon from '@/icons/VisaIcon.svg';
    import HiperCardIcon from '@/icons/HiperCardIcon.svg';
    import MasterCardIcon from '@/icons/MasterCardIcon.svg';
    import ProductFooter from '@/components/ProductFooter.vue';
    import ProductHeader from '@/components/ProductHeader.vue';
    import { StarIcon, ChevronUpIcon } from '@heroicons/vue/24/solid';
    import { Disclosure, DisclosureButton, DisclosurePanel } from '@headlessui/vue';
    import { ArrowLeftIcon, ArrowRightIcon, ShieldCheckIcon } from '@heroicons/vue/24/outline';

    const route = useRoute();

    const data = ref(null);
    const currentImage = ref(0);

    function previousImage() {
        if (currentImage.value > 0) {
            currentImage.value--;
        } else {
            currentImage.value = data.value.images.details.length - 1;
        }
    }

    function nextImage() {
        if (currentImage.value < data.value.images.details.length - 1) {
            currentImage.value++;
        } else {
            currentImage.value = 0;
        }
    }

    onMounted(() => {
        axios.get('/products/' + route.params.slug)
            .then(response => {
                data.value = response.data.data;

                setInterval(nextImage, 5000);
            });
    });
</script>

<template>
    <div v-if="data" class="w-full max-w-screen-sm min-h-screen flex flex-col justify-start items-center mx-auto">
        <ProductHeader :data="{ ...data['header'], 'images': data['images']['header'] }" class="mb-10" />

        <section class="w-full px-3 mb-10">
            <img :src="data.images.details[currentImage]" :alt="'Image ' + currentImage" class="object-cover w-auto max-h-96 rounded-2xl border shadow mx-auto">

            <div class="flex justify-between items-center mt-4">
                <ArrowLeftIcon class="w-6 h-6 cursor-pointer" @click="previousImage()" />

                <div class="flex justify-center items-center gap-2">
                    <span v-for="(image, index) in data.images.details" :key="index" class=" rounded-full block w-2 h-2" :class="index == currentImage ? 'bg-black' : 'bg-neutral-300'"></span>
                </div>

                <ArrowRightIcon class="w-6 h-6 cursor-pointer" @click="nextImage()" />
            </div>
        </section>

        <section class="w-full px-3 mb-10">
            <h1 class="text-lg font-medium mb-2">{{ data.details.name }}</h1>

            <div class="flex justify-start items-end gap-2 mb-4">
                <h1 class="text-2xl font-medium">{{ data.details.final_price }}</h1>

                <div v-if="data.details.original_price">
                    <span v-if="data.details.original_price" class="text-base line-through font-normal">{{ data.details.original_price }}</span>
                </div>
            </div>

            <button class="w-full h-12 rounded-lg flex justify-center items-center gap-2 font-medium shadow" @click="redirectToCheckout()" :style="{ 'backgroundColor': data.details.buy_button.background_color, 'color': data.details.buy_button.text_color }">
                {{ data.details.buy_button.text }}
            </button>

            <div class="mt-4 flex flex-col justify-center items-center">
                <div class="w-full">
                    <h1 class="bg-[#F6F6F8] text-center p-1 rounded text-xs mb-1">MÉTHODES DE PAYEMENT</h1>

                    <div class="flex justify-center items-center gap-2">
                        <VisaIcon class="w-fit h-auto" />

                        <MasterCardIcon class="w-fit h-auto" />

                        <HiperCardIcon class="w-fit h-auto" />

                        <EloIcon class="w-fit h-auto" />

                        <AmexIcon class="w-fit h-auto" />
                    </div>
                </div>

                <div class="flex justify-start items-center gap-3 border px-2 py-4 rounded w-full">
                    <ShieldCheckIcon class="w-8 h-8" />

                    <p class="text-xs text-[#8F8F8F]">
                        Les paiements et les informations sont sécurisés.

                        <br>

                        Site officiel avec tous droits réservés.
                    </p>
                </div>

                <div class="flex justify-start items-center gap-3 mt-2 border px-2 py-4 rounded w-full">
                    <ShieldCheckIcon class="w-8 h-8" />

                    <p class="text-xs text-[#8F8F8F]">
                        <span class="text-black font-bold">Retours sans frais</span>

                        <br>

                        Remboursement à 100% de votre argent

                        <br>

                        7 jours après réception de la marchandise.
                    </p>
                </div>
            </div>
        </section>

        <hr class="w-full mb-10">

        <section class="w-full px-3 mb-10">
            <div class="p-3">
                <Disclosure v-slot="{ open }" :defaultOpen="true">
                    <DisclosureButton class="w-full flex justify-between items-center">
                        <h1 class="text-[#484848] font-semibold">Description</h1>

                        <ChevronUpIcon class="w-5 h-5" :class="open ? 'rotate-180 transform' : ''" />
                    </DisclosureButton>

                    <DisclosurePanel class="px-3 flex flex-col mt-3 text-gray-500">
                        <div v-html="data.details.description"></div>
                    </DisclosurePanel>
                </Disclosure>
            </div>
        </section>

        <hr class="w-full mb-10">

        <section class="w-full px-3 mb-10">
            <div class="w-full">
                <h1 class="p-1 font-medium rounded mb-1">MÉTHODES DE PAYEMENT</h1>

                <div class="flex justify-start items-center gap-2">
                    <VisaIcon class="w-fit h-auto" />

                    <MasterCardIcon class="w-fit h-auto" />

                    <HiperCardIcon class="w-fit h-auto" />

                    <EloIcon class="w-fit h-auto" />

                    <AmexIcon class="w-fit h-auto" />
                </div>
            </div>

            <hr class="w-full my-2">

            <div class="w-full">
                <h1 class="p-1 font-medium rounded mb-1">MAGASIN VÉRIFIÉ</h1>

                <div class="flex justify-start items-center gap-4">
                    <a href="#">
                        <div style="margin-left: -5px !important;">
                            <img class="w-20 h-auto" data-savepage-currentsrc="https://loja-arezzo.store/1/1/images/google-site-seguro-pt.svg" data-savepage-src="images/google-site-seguro-pt.svg" src="data:image/svg+xml;base64," width="110" height="40" alt="Selo do Google">
                        </div>
                    </a>

                    <a href="#">
                        <div>
                            <img class="w-20 h-auto" data-savepage-currentsrc="https://loja-arezzo.store/1/1/images/Reclameaqui.svg" data-savepage-src="images/Reclameaqui.svg" src="data:image/svg+xml;base64," width="90" height="35" alt="Selo do Reclame Aqui">
                        </div>
                    </a>

                    <a href="#">
                        <div>
                            <img class="w-20 h-auto" data-savepage-currentsrc="https://loja-arezzo.store/1/1/images/SSL.svg" data-savepage-src="images/SSL.svg" src="data:image/svg+xml;base64," width="80" height="35" alt="SLL Seguro">
                        </div>
                    </a>
                </div>
            </div>
        </section>

        <ProductFooter :data="{ ...data['footer'], 'images': data['images']['footer'] }" />
    </div>
</template>
